import React from 'react'
import {useState} from 'react'


const Button = (props) => {
  return(
    <button onClick={props.onClick}>{props.text}</button>
  );
}

const Stats = (props) => {
  return(
    <>
      <p>{props.text} {props.val}</p>
    </>
  );
}


const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleClick = (val) => () => {
    if(val === 'g') {
      setGood(good+1);
    } else if(val === 'n') {
      setNeutral(neutral+1);
    }else{
      setBad(bad+1)
    }
  }

  return(
    <div>
      <h1>give feedback</h1>
      <Button onClick={handleClick('g')} text={"Good"}/>
      <Button onClick={handleClick('n')} text={"Neutral"}/>
      <Button onClick={handleClick('b')} text={"Bad"}/>
      <h2>statistics</h2>
      <Stats text={"Good"} val={good}/>
      <Stats text={"Neutral"} val={neutral}/>
      <Stats text={"Bad"} val={bad}/>
      <Total />
    </div>
  );
}

export default App;
