import React from 'react'
import {useState} from 'react'


const Button = (props) => {
  return(
    <button onClick={props.onClick}>{props.text}</button>
  );
}

const Total = (props) => {
  let total = props.score[0] + props.score[1] + props.score[2];
  return (
    <>
      <p>all {total}</p>
      <p>positive {props.score[0] > 0 ? (props.score[0]/total)*100 : 0}</p>
    </>
  );
}

const Stats = (props) => {
  if((props.val[0] > 0)  || (props.val[1] > 0) || (props.val[2] > 0)){
  return(
    <>
      <p>{props.text[0]} {props.val[0]}</p>
      <p>{props.text[1]} {props.val[1]}</p>
      <p>{props.text[2]} {props.val[2]}</p>
      <Total score={[props.val[0], props.val[1], props.val[2]]}/>
    </>
  );
  }
    return(
      <>
        <p>No feedback given.</p>
      </>
    );
}




const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleClick = (val) => () => {
    if(val === 'g') {
      setGood(good+1);
    } else if(val === 'n') {
      setNeutral(neutral+1);
    }else{
      setBad(bad+1)
    }
  }

  return(
    <div>
      <h1>give feedback</h1>
      <Button onClick={handleClick('g')} text={"Good"}/>
      <Button onClick={handleClick('n')} text={"Neutral"}/>
      <Button onClick={handleClick('b')} text={"Bad"}/>
      <h2>statistics</h2>
      <Stats text={["Good", "Neutral", "Bad"]} val={[good, neutral, bad]}/>

    </div>
  );
}

export default App;
