import React from 'react'
import {useState} from 'react'

const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  handleClick = (val) => {
    if(val == 'g') {
      setGood(good+1);
    } else if(val == 'n') {
      setNeutral(neutral+1);
    }else{
      setBad(bad+1)
    }
  }

  return(
    <div>
      <Header />
      <Button onClick={handleClick('g')}>{text}</Button>
      <Button onClick={handleClick('n')}>{text}</Button>
      <Button onClick={handleClick('b')}>{text}</Button>
    </div>
  );
}
